import { SvgEditor } from 'highway-ui';
import { useState } from 'react';
import { mockDrawData } from '../../../src/components/SvgEditor/mockDrawData';

const SvgEditorDemo = (props) => {
  const data = mockDrawData;
  const [screenIndex, setScIndex] = useState<number>(0);

  const handleDataChange = (data) => {
    console.log(data);
  };

  const handleScreenIndexChange = (index) => {
    console.log(index);
    setScIndex(Number(index));
  };
  return (
    <div>
      <SvgEditor
        data={data}
        width={400}
        height={400}
        source="view"
        scale={1}
        screenNum={1}
        screenIndex={screenIndex}
        onDataChange={handleDataChange}
        onScreenIndexChange={handleScreenIndexChange}
      ></SvgEditor>
    </div>
  );
};

export default SvgEditorDemo;
