/**
 * 计算多边形宽高
 * @param points 'x,y x1,y1'
 * @returns {Array} [width, heigth]
 */
var calcPolygonWidthHeight = function calcPolygonWidthHeight(points) {
  var maxWidth = 0;
  var minWidth = 0;
  var maxHeight = 0;
  var minHeight = 0;
  var xArr = [];
  var yArr = [];
  points.split(' ').forEach(function (point) {
    // potin 'x,y'
    var pointArr = point.split(',');
    xArr.push(pointArr[0]);
    yArr.push(pointArr[1]);
  });
  maxWidth = Math.max.apply(Math, xArr);
  minWidth = Math.min.apply(Math, xArr);
  maxHeight = Math.max.apply(Math, yArr);
  minHeight = Math.min.apply(Math, yArr);
  var width = maxWidth - minWidth;
  var height = maxHeight - minHeight;
  return [width, height];
};

export default calcPolygonWidthHeight;